import React, { useContext, useState} from 'react'
import {Form, Button, Dropdown} from "react-bootstrap";
import Modal from 'react-bootstrap/Modal';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';

const Setting = observer(({show, onHide}) => {
    const {info} = useContext(Context)

    const [nikname, setNikname] = useState(info.user.nikname)
    const [login, setLogin] = useState(info.user.email)
    const [costEl, setCostEl] = useState(info.uslov.cost_el)

    return (
        <Modal
        show = {show}
        onHide={onHide}
        size = "lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Настройки
          </Modal.Title>
        </Modal.Header>


        <Modal.Body>

            <Form className='mt-2'>
                <Form.Label>
                    Имя пользователя:
                </Form.Label>
                <Form.Control
                    value={nikname}
                    onChange={e => setNikname(e.target.value)}
                    placeholder={"Имя пользователя"}
                />
            </Form>

            <Form className='mt-2'>
                <Form.Label>
                    Логин:
                </Form.Label>
                <Form.Control
                    value={login}
                    onChange={e => setLogin(e.target.value)}
                    placeholder={"Логин"}
                />
            </Form>

            <Form className='mt-2'>
                <Form.Label>
                    Стоимость электричества:
                </Form.Label>
                <Form.Control
                    value={costEl}
                    onChange={e => setCostEl(e.target.value)}
                    placeholder={"Логин"}
                />
            </Form>



        </Modal.Body>


        <Modal.Footer>
          <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
          <Button variant="outline-success" onClick={onHide}>Добавить</Button>
        </Modal.Footer>
      </Modal>
    );
});

export default Setting;
