import React, {useContext, useEffect} from "react";
import {Container, Row, Col, Card} from 'react-bootstrap'
import InfoPanel from '../components/InfoPanel'
import MainersPanel from "../components/MainersPanel";
import { fetchMainers } from "../http/mainerAPI";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { fetchUslov } from "../http/uslovAPI";

const Server = observer(() => {
    const {info} = useContext(Context)

    useEffect(()=>{
        fetchMainers(3).then(data => info.setMainers(data))
        fetchUslov(2).then(data => info.setUslov(data))
    }, [])

    
    return (
        <Container>
            <Row className="mt-2"> 
                <Col md = {3}>
                    <InfoPanel/>
                </Col>
                <Col md = {9}>
                    <MainersPanel/>
                </Col>
            </Row>
        </Container>
    );
});

export default Server;
