import React, { useContext } from "react";
import { Card , ListGroup, Image} from "react-bootstrap";
import { Context } from "../index";
import defaut from "../img/defaut.jpg"
import { useNavigate } from "react-router-dom";
import { MAINER_ROUTER } from "../utils/consts";
import grinIO from "../img/grinIO.png"
import redIO from "../img/redIO.png"

const MainersPanel = () => {
    const {info} = useContext(Context)
    const navigator = useNavigate()
    return (
        <Card 
            style={{borderRadius: 20}}
        >
            <ListGroup>
                {info.mainers.map(mainer =>
                    <Card 
                        style={{margin: 20}}
                    >
                        <div className="d-flex">
                        <Image
                            src = {defaut}
                            width={90}
                            height={90}
                            roundedCircle
                            style={{margin: 20, cursor: 'pointer' , objectFit: 'cover'}}
                            onClick={() => navigator(MAINER_ROUTER+ '/' + mainer.id)}
                        ></Image>
                        {mainer.work?
                        <Image
                            src={grinIO}
                            width={90}
                            height={90}
                            style={{margin: 20, cursor: 'pointer' , objectFit: 'cover', marginLeft: 'auto'}}
                            onClick={() => mainer.work =  !mainer.work}
                        ></Image>
                        :
                        <Image
                            src={redIO}
                            width={90}
                            height={90}
                            style={{margin: 20, cursor: 'pointer' , objectFit: 'cover', marginLeft: 'auto'}}
                            onClick={() => mainer.work =  !mainer.work}
                        ></Image>
                        }
                        </div>
                    </Card> 
                )}   
            </ListGroup>
        </Card>
    )
}

export default MainersPanel;
