import React, {useEffect, useState} from "react";
import { Card } from "react-bootstrap";
import { Line, Chart } from "react-chartjs-2";
import "chart.js/auto";
import { getGraf } from "../http/mainerAPI";
import { useParams } from 'react-router-dom';




const Graf = () => {
    const [graf,setGraf] = useState({})
    const {id} = useParams()

    useEffect(()=>{
      getGraf(id).then(data => console.log(data))
    }, [])


    console.log(graf)
    // for (var index = 0; index < graf.length; ++index) {
    //   console.log(graf[index]);
    // }
    

    const lineChartData = {
        labels: ["October", "November", "December", "Octoberr"],
        datasets: [
          {
            data: [8137119, 9431691, 10266674, 9431691],
            label: "Доход",
            borderColor: "#3333ff",
            backgroundColor: "#3333ff",
            lineTension: 0.5
          },
          {
            data: [1216410, 1371390, 1477380,  1371390],
            label: "Расход",
            borderColor: "#ff3333",
            backgroundColor: "#ff3333",
            lineTension: 0.5
          },
          {
            data: [1316410, 1271390, 1577380,  1171390],
            label: "Прибыль",
            borderColor: "#0DFC00",
            backgroundColor: "#0DFC00",
            lineTension: 0.5
          }
        ]
      };

    return (
        <div style={{marginTop: 100}}>
            <Card>
              <Line
                type="line"
                options={{
                  title: {
                  display: true,
                  text: "График работы машины:",
                  fontSize: 20
                },
                  legend: {
                  display: true, //Is the legend shown?
                  position: "top" //Position of the legend.
                }
                }}
                data={lineChartData}
              />
            </Card>
        </div>
    )
}

export default Graf;
